# -*- coding: utf-8 -*-
import base64, struct, re
bmp_data = base64.b64decode('')

def bmp_info(data):
    part_bmp_data = data[:30]
    s = struct.unpack('<ccIIIIIIHH', part_bmp_data)
    print(s)
    return{
        'width': s[-4],
        'height': s[-3],
        'color': s[-1]
    }

# test:
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('OK')
